Ext.define('W5D5_Project.controller.BrowseProductsController', {
    extend: 'Ext.app.Controller',

    control: {
        "#browseProductsField": {
            change: 'onBrowseProductsSearchChange'
        }
    },

    onBrowseProductsSearchChange: function(field, newValue, oldValue, eOpts) {
        var productSearch, prodStore;
        productSearch = Ext.getCmp('browseProducts').getValue();
        prodStore = Ext.getStore('ProductStore');

        if(Ext.isEmpty(productSearch)){
            prodStore.clearFilter();
        } else{
            prodStore.filter('prodName',productSearch);
        }
    }

});
