Ext.define('W3D5_Project.controller.Login', {
    extend: 'Ext.app.Controller',

    id: 'Login',

    onLoginbuttonClick: function() {
        var store =Ext.getStore('UserStore');
        var valid = false;
        var username = Ext.getCmp('loginusername').getValue();
        var password = Ext.getCmp('loginpassword').getValue();
        Ext.each(store.data.items, function(rec){
            if(rec.data.username==username && rec.data.password==password){
                valid = true;
            }

        });
        if(valid){
            if(Ext.isEmpty(this.mainWindow)){
            this.mainWindow = Ext.create('W3D5_Project.view.MainViewport', {});
        }
        this.mainWindow.show();
        this.mainWindow.unmask();
        }else{
        Ext.MessageBox.show({
            title: 'Failed Login',
            msg: 'Please enter a registered account',
            buttons: Ext.Msg.OK,
            icon: Ext.Msg.WARNING

        });

        }
    },

    onRegisterbuttonClick: function() {
        if(Ext.isEmpty(this.gridWin)){
            this.gridWin = Ext.create('W3D5_Project.view.RegisterViewPort', {});
        }
        this.gridWin.show();
        this.gridWin.unmask();
    },

    init: function(application) {
        this.control({
            "#loginbutton": {
                click: this.onLoginbuttonClick
            },
            "#registerbutton": {
                click: this.onRegisterbuttonClick
            }
        });
    }

});
